import Foundation
import UIKit
import RxSwift
import RxCocoa
import RxDataSources

class MainViewModel {
    var disposeBag: DisposeBag = DisposeBag()
    var mainScrollViewRefreshControl: UIRefreshControl? = nil

    private let loadRestaurantUseCase: LoadRestaurantUseCase
    private let loadSelectedFeelingsUseCase: LoadSelectedFeelingsUseCase
    private let loadRemoteUrlPresetDataUseCase: LoadRemoteUrlPresetDataUseCase

    init(loadRestaurantUseCase: LoadRestaurantUseCase, loadSelectedFeelingsUseCase: LoadSelectedFeelingsUseCase, loadRemoteUrlPresetDataUseCase: LoadRemoteUrlPresetDataUseCase) {
        self.loadRestaurantUseCase = loadRestaurantUseCase
        self.loadSelectedFeelingsUseCase = loadSelectedFeelingsUseCase
        self.loadRemoteUrlPresetDataUseCase = loadRemoteUrlPresetDataUseCase
    }

    var disappearMainScrollLoadingView: (() -> ())!

    let restaurantCategoryItems = BehaviorSubject<[RestaurantCategory]>(value: [])
    let seletedFeelingsSequence: BehaviorRelay<[Feeling]> = BehaviorRelay<[Feeling]>(value: [])
    let remoteUrlPresetDataSequence: BehaviorRelay<[URL]> = BehaviorRelay<[URL]>(value: [])

    let restaurantDessertKind = ["카페", "디저트"]
    let recommendsRestaurnatsDataCount = 10

    var restaurantCategoryArray: [RestaurantCategory] {
        get {
            return [RestaurantCategory](arrayLiteral:
                RestaurantCategory(title: StringConstants.suggestRestaurantMessage, items: suggestRestaurantsData!),
                RestaurantCategory(title: StringConstants.recommendsRestaurantMessage, items: recommendsRestaurantsData!),
                RestaurantCategory(title: StringConstants.suggestDessertMessage, items: suggestDessertRestaurantsData!),
                RestaurantCategory(title: StringConstants.suggestOtherRestaurantMessage, items: restaurantsData!)
            )
        }
    }

    var restaurantsData: Restaurant!

    var suggestRestaurantsData: Restaurant! {
        get {
            var items: [RestaurantItem]! = []

            seletedFeelingsSequence.bind(onNext: { (seletedFeelings) in
                seletedFeelings.forEach { feeling in
                    self.restaurantsData.data?.forEach({ restaurant in
                        let restaurantFeelingTags = restaurant.feeling_tags ?? []
                        if restaurantFeelingTags.contains(feeling.feelName) {
                            items.append(restaurant)
                        }
                    })
                }
            }).disposed(by: disposeBag)

            return Restaurant(data: items.uniqued())
        }
    }

    var recommendsRestaurantsData: Restaurant! {
        get {
            var items: [RestaurantItem]! = []
            var needRecommend: Bool = true

            self.restaurantsData.data?.forEach({ restaurant in
                if needRecommend == false {
                    return
                }

                guard restaurant.custom != nil else {
                    return
                }

                restaurant.custom!.forEach({ value in
                    if value == "NoRecommend" {
                        needRecommend = false
                        return
                    }
                })
            })

            if needRecommend == true {

                let dateFormatter = DateFormatter()
                dateFormatter.dateFormat = "yyyyMMdd"

                let todaySeed = Int(dateFormatter.string(from: Date()))!
                var seedGenerator = RandomNumberGeneratorWithSeed(seed: todaySeed)

                for _ in 0...recommendsRestaurnatsDataCount {
                    items.append(restaurantsData.data!.randomElement(using: &seedGenerator)!)
                }

            }

            return Restaurant(data: items)
        }
    }

    var suggestDessertRestaurantsData: Restaurant! {
        get {
            var items: [RestaurantItem]! = []

            self.restaurantsData.data?.forEach({ restaurnt in
                restaurantDessertKind.forEach({ dessertKind in
                    guard restaurnt.kind != nil else {
                        return
                    }

                    if restaurnt.kind!.contains(dessertKind) {
                        items.append(restaurnt)
                    }
                })
            })

            return Restaurant(data: items.uniqued())
        }
    }

    var selectedFeelings: [Feeling] = []
    var remoteUrlPresetData: [URL] = []

    // MARK: - Public func

    func loadRestaurants(remoteJsonUrl: URL? = nil, needRefresh: Bool = false) {
        restaurantsData = loadRestaurantUseCase.call(
            remoteJsonUrl: remoteJsonUrl,
            needRefresh: needRefresh,
            downloadCompletion: disposeRestaurantRemoteLoadingViews,
            refreshedCompletion: {
                self.loadRestaurants()
                self.fetchRestaurantCategoryItems()
            }
        )
    }

    func disposeRestaurantRemoteLoadingViews() {
        mainScrollViewEndRefresh()
        disappearMainScrollLoadingView()
    }

    func mainScrollViewEndRefresh() {
        DispatchQueue.main.async { [self] in
            guard mainScrollViewRefreshControl != nil else {
                return
            }
            mainScrollViewRefreshControl!.endRefreshing()
            mainScrollViewRefreshControl = nil
        }
    }

    func fetchRestaurantCategoryItems() {
        print("Fetch Restaurant Caregory Items")
        restaurantCategoryItems.onNext(restaurantCategoryArray)
    }

    func loadFeelings() {
        selectedFeelings = loadSelectedFeelingsUseCase.call()
    }

    func fetchSelectedFeelings() {
        self.seletedFeelingsSequence.accept(selectedFeelings)
    }

    func loadRemoteUrlPresetData() {
        remoteUrlPresetData = loadRemoteUrlPresetDataUseCase.call()
    }

    func addRemoteUrlPresetData(url: URL = URL(string: StringConstants.setRemoteUrlTextFieldPlaceholder)!) {
        remoteUrlPresetData.append(url)
        self.saveRemoteUrlPresetData()
        self.fetchRemoteUrlPresetData()
    }

    func editRemoteUrlPresetData(index: Int, url: URL) {
        remoteUrlPresetData[index] = url
        self.saveRemoteUrlPresetData()
        self.fetchRemoteUrlPresetData()
    }

    func deleteRemoteUrlPresetData(index: Int) {
        remoteUrlPresetData.remove(at: index)
        self.saveRemoteUrlPresetData()
        self.fetchRemoteUrlPresetData()
    }

    func removeAllRemoteUrlPresetData() {
        remoteUrlPresetData = []
        self.saveRemoteUrlPresetData()
        self.fetchRemoteUrlPresetData()
    }

    func saveRemoteUrlPresetData() {
        loadRemoteUrlPresetDataUseCase.save(remoteUrlPresetData: self.remoteUrlPresetData)
    }

    func fetchRemoteUrlPresetData() {
        self.remoteUrlPresetDataSequence.accept(remoteUrlPresetData)
    }

    func getRestaurantCategoryArrayCount() -> Int {
//        return restaurantCategoryArray.count
        return 4
    }
}
