import Foundation
import UIKit

extension UICollectionView {
    func deleteAllItems() {
        for section in 0...numberOfSections {
            for item in 0...numberOfItems(inSection: section) {
                deleteItems(at: [IndexPath(row: item, section: section)])
                continue
            }
        }
    }
}
