import Foundation
import RxRelay

class LoadRestaurantUseCase {
    let restaurantRepository: RestaurantRepository

    init(restaurantRepository: RestaurantRepository) {
        self.restaurantRepository = restaurantRepository
    }

    func call(remoteJsonUrl: URL?, needRefresh: Bool = false, downloadCompletion: @escaping() -> Void, refreshedCompletion: @escaping() -> Void) -> Restaurant {
        return restaurantRepository.getRestaurantsData(
            remoteJsonUrl: remoteJsonUrl,
            needRefresh: needRefresh,
            downloadCompletion: downloadCompletion,
            refreshedCompletion: refreshedCompletion)
    }
}
