import Foundation
import UIKit

extension UILabel {
    func setLineSpacing(_ value: CGFloat = CGFloat(6)) {
        let attrString = NSMutableAttributedString(string: text!)
        let paragraphStyle = NSMutableParagraphStyle()
        paragraphStyle.lineSpacing = value
        attrString.addAttributes([NSAttributedString.Key.paragraphStyle: paragraphStyle], range: NSMakeRange(0, attrString.length))
        attributedText = attrString
    }
}
