import Foundation
import MapKit

extension MKMapView {
    func setPlaceMark(address: String, placemarkTitle: String = "", placemarkSubTitle: String = "", placemarkPinImage: UIImage = #imageLiteral(resourceName: "map_pin")) {
        let address: String = address
        let geocoder: CLGeocoder = CLGeocoder()

        geocoder.geocodeAddressString(address, completionHandler: {(placemarks, error) -> Void in
            if placemarks?.count ?? 0 > 0 {
                let topResult: CLPlacemark = placemarks![0];
                let placemark: MKPointAnnotation = MKPointAnnotation()

                placemark.coordinate = topResult.location!.coordinate
                placemark.title = placemarkTitle
                placemark.subtitle = placemarkSubTitle

//                let placemarkImageAnnotationView = setPlacemarkImage(mapView: self, viewForAnnotation: placemark, placemarkIamge: placemarkPinImage)
//                self.addAnnotation(placemarkImageAnnotationView as! MKAnnotation)

                var region: MKCoordinateRegion = self.region;
                region.center = placemark.coordinate
                region.span.longitudeDelta = NumberConstants.placemarkDelta
                region.span.latitudeDelta = NumberConstants.placemarkDelta
                self.setRegion(region, animated: true)
                self.addAnnotation(placemark)
            }
        })
    }
}
