import UIKit
import Foundation
import UIKit
import SnapKit
import RxSwift

import STAntiCapture

class BaseViewController: UIViewController {
    var disposeBag: DisposeBag = DisposeBag()
    var antiCaptureView: STAntiCaptureView!

    private static var _sharedAntiCapture: Bool = true
    public static var sharedAntiCapture: Bool {
        get {
            return BaseViewController._sharedAntiCapture
        }
        set {
            if newValue == BaseViewController._sharedAntiCapture { return }
            BaseViewController._sharedAntiCapture = newValue
            NotificationCenter.default.post(name: .sharedAntiCaptureChanged, object: nil)
        }
    }

    private static var _sharedBackgroundColorOfCaptured: UIColor = .black
    public static var sharedBackgroundColorOfCaptured: UIColor {
        get {
            return BaseViewController._sharedBackgroundColorOfCaptured
        }
        set {
            if newValue == BaseViewController._sharedBackgroundColorOfCaptured { return }
            BaseViewController._sharedBackgroundColorOfCaptured = newValue
            NotificationCenter.default.post(name: .sharedAntiCaptureChanged, object: nil)
        }
    }

    private static var _sharedBackgroundColorOfAppSwitcherView: UIColor = .black
    public static var sharedBackgroundColorOfAppSwitcherView: UIColor {
        get {
            return BaseViewController._sharedBackgroundColorOfAppSwitcherView
        }
        set {
            if newValue == BaseViewController._sharedBackgroundColorOfAppSwitcherView { return }
            BaseViewController._sharedBackgroundColorOfAppSwitcherView = newValue
            NotificationCenter.default.post(name: .sharedAntiCaptureChanged, object: nil)
        }
    }

    private static var _sharedDisableAppSwitcherViewOnce: Bool = false
    public static var sharedDisableAppSwitcherViewOnce: Bool {
        get {
            return BaseViewController._sharedDisableAppSwitcherViewOnce
        }
        set {
            if newValue == BaseViewController._sharedDisableAppSwitcherViewOnce { return }
            BaseViewController._sharedDisableAppSwitcherViewOnce = newValue
            NotificationCenter.default.post(name: .sharedAntiCaptureChanged, object: nil)
        }
    }

    deinit {
        disposeBag = DisposeBag()
        NotificationCenter.default.removeObserver(self)
    }

    override func viewDidLoad() {
        super.viewDidLoad()

        setupVC()
        setupVM()
        setupBind()

    }

    func setupVC() {

    }

    func setupVM() {

    }

    func setupBind() {

    }

    func setupAntiCaptureView() {
        antiCaptureView = STAntiCaptureView()
        view.addSubview(antiCaptureView)

        antiCaptureView.translatesAutoresizingMaskIntoConstraints = false
        antiCaptureView.snp.makeConstraints { make in
            make.top.leading.trailing.bottom.equalToSuperview()
        }

        antiCaptureView.antiCapture = BaseViewController._sharedAntiCapture
        antiCaptureView.customImage = UIImage(named: "Stealien")
        antiCaptureView.backgroundColor = view.backgroundColor
        antiCaptureView.backgroundColorOfCaptured = BaseViewController._sharedBackgroundColorOfCaptured
        antiCaptureView.backgroundColorOfAppSwitcherView = BaseViewController._sharedBackgroundColorOfAppSwitcherView
        antiCaptureView.disableAppSwitcherViewOnce = BaseViewController._sharedDisableAppSwitcherViewOnce

        NotificationCenter.default.addObserver(self, selector: #selector(handleSharedAntiCaptureChanged), name: .sharedAntiCaptureChanged, object: nil)
    }

    @objc func handleSharedAntiCaptureChanged() {
        antiCaptureView.antiCapture = BaseViewController._sharedAntiCapture
        antiCaptureView.backgroundColorOfCaptured = BaseViewController._sharedBackgroundColorOfCaptured
        antiCaptureView.backgroundColorOfAppSwitcherView = BaseViewController._sharedBackgroundColorOfAppSwitcherView
        antiCaptureView.disableAppSwitcherViewOnce = BaseViewController._sharedDisableAppSwitcherViewOnce
    }
}

extension Notification.Name {
    static let sharedAntiCaptureChanged = Notification.Name("sharedAntiCaptureChanged")
}
